import './style/index.less';
import {Component, DataTypes} from 'san';

const prefixCls = 'w-price';

export default class WujiPrice extends Component {
    static template = /* html */ `
        <div class="${prefixCls}-wrap ${prefixCls}-{{size}}">
            <div class="${prefixCls}-value">{{value}}</div>
            <div class="${prefixCls}-sales">{{sales}}</div>
        </div>
    `;
    static dataTypes = {
        size: DataTypes.oneOf([
            'md',
            'sm',
        ]),
        value: DataTypes.string,
        sales: DataTypes.string,
    };

    initData() {
        return {
            size: 'md',
            value: '',
            sales: '',
        };
    }
}
