/**
 * @file 组件 WujiSingleHead 单头像
 * @author raowenjuan <raowenjuan@baidu.com>
 */

import './style/index.less';
import {Component} from 'san';
import WujiAvatar from '../wuji-avatar';

const prefixCls = 'w-single-head';

export default class WujiSingleHead extends Component {
    static template = /* html */ `
        <div class="${prefixCls}" on-click="handleClick">
            <div class="${prefixCls}-wrap">
                <wuji-avatar class="avatar-wrap" src="{{icon}}" size="{{size}}"/>
                <div class="${prefixCls}-title-wrap">
                    <span class="${prefixCls}-title">{{title}}</span>
                </div>
                <div class="${prefixCls}-text-wrap">
                    <span s-if="text1" class="${prefixCls}-text1 {{text1.color}}">{{text1.text}}</span>
                    <span
                        s-if="text2 && text2.text"
                        class="${prefixCls}-text2 {{text2.color}}"
                    >{{text2.text}}</span>
                </div>
            </div>
            <span class="${prefixCls}-button">{{btnText}}</span>
        </div>
    `;

    static components = {
        'wuji-avatar': WujiAvatar,
    };

    initData() {
        return {
            size: 66,
        };
    }

    handleClick() {
        console.log('dom clicked!'); // eslint-disable-line
    }
}
