import {Component} from 'san';

class List2d extends Component {
    static template = /* html */ `
        <div class="list2d">
            <div s-for="list in list2d" class="lineInList">
                <template s-for="item, index in list">
                    <div s-if="vline && index > 0 && item" class="vline"/>
                    <div class="{{lineClass}}">{{item}}</div>
                </template>
            </div>
        </div>
    `;
}

export default List2d;
