import './style/index.less';
import {Component} from 'san';
import Detail from './components/Detail';
import Footer from './components/Footer';
import ImgExt from './components/ImgExt';
import Layout from './components/Layout';

const prefixCls = 'w-smallapp-container';

class WujiSmallappContainer extends Component {
    static template = /* html */ `
        <led-layout s-bind="imageText" class="{{prefixCls}}">
            <!-- 图片扩展信息 -->
            <led-imgext
                s-if="imgExt"
                s-bind="imgExt"
                slot="imageMask"
            />
            <!-- 结构化数据详情 -->
            <led-detail
                s-if="detailInfo"
                s-bind="detailInfo"
                slot="detail"
            />
            <!-- 底部标签 -->
            <led-footer
                s-if="footer"
                s-bind="footer"
                slot="footer"
            />
        </led-layout>
    `;

    static components = {
        'led-detail': Detail,
        'led-footer': Footer,
        'led-imgext': ImgExt,
        'led-layout': Layout,
    };

    initData() {
        return {
            imageText: {},
            imgExt: {},
            prefixCls,
        };
    }
}

export default WujiSmallappContainer;
