/**
 * 计算字符串长度，中文及全角字符等算作两个字节长度
 * @function byteLen
 * @param  {String} string 字符串
 * @return {Number} len 字符串字节长度
 * @example
 * byteLen('中国');//4
 */
export default string => {
    return String(string).replace(/[^\x00-\xff]/gi, '**').length;
};
