import {invokeP} from '../invoke';
import json2params from '../json2params';

/**
 * @function sign
 * @param {object} options - 调起参数
 * @param {number} upgrade - 低版本是否强制升级，默认是**强制**
 * @return {Promise} promise - Promise
 */
export default (options, upgrade = 1) => {
    return invokeP('v4/account/profile/sign', {
        params: json2params(options, true),
        upgrade
    });
};
