/**
 * @file NA 评论组件刷新
 * @module comment
 * @author panming <panming@baidu.com>
 */

import hybrid from '../hybrid/index';
import {getParams} from '../utils';
import register from '../register';

register('comment/refresh', {
    v: 12,
    androidVersionRange: ['9.1.5'],
    filter() {
        return {
            module: 'comment',
            action: 'refresh'
        };
    }
});
/**
 * 评论组件刷新
 * @function context
 * @param {object} params
 * @return {Promise}
 */
export default data => hybrid(getParams('comment-refresh', data));
