import register from '../register';
import {get} from './index';
import {getParams} from '../utils';
register('message/getChatUnreadNum', {
    v: 14,
    versionRange: ['9.3'],
    filter(module, action, data) {
        return {
            action: 'getChatUnReadNum',
            module: 'message',
            data
        };
    }
});
/**
 * 获取 getChatUnreadNum
 * @function getChatUnreadNum
 */
export default (data = {}) => get(getParams('message-chatUnreadNum', data));
