/**
 * @file getFeedSpeedLogData
 * @author zhangtingting12 <zhangtingting12@baidu.com>
 */

import {invokeP} from '../invoke';
import {COMMAND_NOT_EXIST} from '../const';
import {isIOS, isAndroid, version_compare, getBoxVersion} from '../detect';

/**
 * 获取客户端性能数据
 * @function getFeedSpeedLogData
 * @return {Promise} promise - Promise
 */
export default () => {
    const version = getBoxVersion();
    if (isAndroid() && version_compare(version, '11.16') >= 0
        || isIOS() && version_compare(version, '11.17') >= 0) {
        return invokeP('utils/getFeedSpeedLog');
    }
    return Promise.reject(COMMAND_NOT_EXIST);
};
