/**
 * @file getGlobalSettings
 * @module data
 * @author panming <panming@baidu.com>
 */

import register from '../register';
import {get} from './index';
register('utils/getGlobalSettings', {
    lite: {
        versionRange: ['2.2.2']
    },
    androidinfo: {
        versionRange: ['1.0']
    },
    iosinfo: {
        versionRange: ['1.1']
    },
    androidmission: {
        versionRange: ['1.0']
    },
    iosmission: {
        versionRange: ['1.0']
    },
    ios: {
        filter() {
            return {
                module: 'utils',
                action: 'getGlobalSettings'
            };
        },
        thunk(res) {
            return {
                status: res.errno === '1' ? 0 : res.errno,
                message: res.errmsg,
                data: res.data
            };
        }
    },
    android: {
        filter() {
            return {
                module: 'Bdbox_android_utils',
                action: 'getGlobalSettings'
            };
        },
        thunk(res) {
            return {
                status: res.errno === '1' ? 0 : res.errno,
                message: res.errmsg,
                data: res.data
            };
        }
    }
});
/**
 * getGlobalSettings
 * @function getGlobalSettings
 */
export default () =>
    get({
        name: 'utils-globalSettings'
    });
