import register from '../register';
import {getParams} from '../utils';
import {get} from './index';

register('theme/getNightMode', {
    v: 16,
    versionRange: ['10.0'],
    androidliteVersionRange: ['1.0'],
    notSupport: ['ios']
});
/**
 * @function getNightMode
 */

export default (params = {}) => get(getParams('theme-nightMode', params));
