import register from '../register';
import {getParams} from '../utils';
import {get} from './index';

register('praise/getPraiseAvailable', {
    v: 25,
    androidVersionRange: ['10.6.5'],
    iosVersionRange: ['10.10'],
    androidliteVersionRange: ['3.3'],
    androidmissionVersionRange: ['1.0'],
    filter() {
        return {
            module: 'praise',
            action: 'praiseAvailable'
        };
    }
});
/**
 * @function getPraiseAvailable
 * @param {Object} [params={}] - 参数
 */
export default (params = {}) => get(getParams('praise-praiseAvailable', params));
