/**
 * @file getToolbarIcons
 * http://bdbox.baidu.com/jssdk/api/dataget.html#gettoolbaricons
 * @module data
 * @author wangyongqing <wangyongqing01@baidu.com>
 */

import register from '../register';
import {get} from './index';
import {getParams} from '../utils';
register('utils/getToolBarIcons', {
    androidlite: {
        versionRange: ['2.2.2']
    },
    androidinfo: {
        versionRange: ['1.0']
    },
    androidmission: {
        versionRange: ['1.0']
    },
    iosmission: {
        versionRange: ['1.0']
    },
    ios: {
        versionRange: ['8.2.3'],
        filter() {
            return {
                extData: {
                    minver: '8.2.3.0'
                }
            };
        },
        thunk(res) {
            return {
                status: res.result,
                data: {
                    data: res.toolids || []
                }
            };
        }
    },
    android: {
        supportFrame: ['hybrid', 'video', 'tags', 'h5'],
        versionRange: ['8.3'],
        filter() {
            return {
                module: 'Bdbox_android_utils',
                action: 'getToolBarIcons'
            };
        },
        thunk(res) {
            return {
                status: 0,
                data: {
                    data: res.toolids || []
                }
            };
        }
    }
});
/**
 * getToolBarIcons
 * @function getToolBarIcons
 * @param {object} [data={}] - getToolBarIcons参数
 */
export default (data = {}) => get(getParams('utils-toolBarIcons', data));
