/**
 * @file 设置 feed 的添加城市频道列表
 * @module data
 * @author wangyongqing <wangyongqing01@baidu.com>
 */
import register from '../register';
import {set} from './index';
import {getParams} from '../utils';

register('feed/setAddCity', {
    v: 19,
    versionRange: ['10.3'],
    androidliteVersionRange: ['3.6'],
    iosinfoVersionRange: ['3.6'],
    iosmissionVersionRange: ['1.0'],
    androidmissionVersionRange: ['1.0'],
    filter() {
        return {
            module: 'feed/tabopt',
            action: 'addCity'
        };
    }
});

/**
 * feed setAddCity
 * @function setAddCity
 * @param {array} [citylist=[]] - feed-addCity citylist
 */
export default (citylist = []) => set(getParams('feed-addCity', {citylist}));
