/**
 * @module data
 * @file 设置删除的feed城市列表
 * @author wangyongqing <wangyongqing01@baidu.com>
 */
import register from '../register';
import {set} from './index';
import {getParams} from '../utils';
register('feed/setDeleteCity', {
    v: 19,
    versionRange: ['10.3'],
    androidliteVersionRange: ['3.6'],
    iosinfoVersionRange: ['3.6'],
    iosmissionVersionRange: ['1.0'],
    androidmissionVersionRange: ['1.0'],
    filter() {
        return {
            module: 'feed/tabopt',
            action: 'deleteCity'
        };
    }
});

/**
 * feed setDeleteCity
 * @function setDeleteCity
 * @param {array} [citylist=[]] - feed-setDeleteCity citylist
 */
export default (citylist = []) => set(getParams('feed-deleteCity', {citylist}));
