/**
 * http://bdbox.baidu.com/jssdk/api/dataset.html#settoolbaricons
 * @file setToolBarIcons
 * @module data
 * @author wangyongqing <wangyongqing01@baidu.com>
 */

import register from '../register';
import {set} from './index';
import {getParams} from '../utils';
register('utils/setToolBarIcons', {
    androidlite: {
        versionRange: ['2.2.2'],
        supportFrame: ['video', 'tags', 'h5'],
        thunk(res) {
            return {
                status: 0,
                message: res.errmsg
            };
        }
    },
    androidmission: {
        versionRange: ['1.0'],
        thunk(res) {
            return {
                status: 0,
                message: res.errmsg
            };
        }
    },
    iosmission: {
        versionRange: ['1.0']
    },
    androidinfo: {
        versionRange: ['1.0'],
        supportFrame: ['video', 'tags', 'h5'],
        thunk(res) {
            return {
                status: 0,
                message: res.errmsg
            };
        }
    },
    iosinfo: {
        versionRange: ['1.1']
    },
    ios: {
        versionRange: ['7.7'],
        supportFrame: ['hybrid', 'video', 'tags', 'h5', 'search'],
        filter() {
            return {
                extData: {
                    minver: '7.7.0.0'
                }
            };
        },
        thunk(res) {
            return {
                status: res.errno === '1' ? 0 : res.errno,
                message: res.errmsg
            };
        }
    },
    android: {
        versionRange: ['7.7'],
        hasCB: false,
        supportFrame: ['hybrid', 'h5', 'video', 'tags'],
        filter() {
            return {
                module: 'Bdbox_android_utils',
                action: 'setToolBarIcons'
            };
        }
    }
});
/**
 * setToolBarIcons
 * @function setToolBarIcons
 * @param {array} [toolids=[]] - setToolBarIconsçš„ toolids
 */
export default (toolids = []) => set(getParams('utils-toolBarIcons', {toolids}));
