import version_compare from '@baidu/xbox/version_compare';
import getBoxVersion from '@baidu/xbox/get-box-version';
import {isIOS, isAndroid} from '@baidu/xbox/os';
import isBox from '@baidu/xbox/is-box';
import isMatrix from '@baidu/xbox/is-matrix';
/**
 * 或判断是否是某个 box
 * @param {string} subnames - lite/info组合，[,]隔开
 * @param {*} ua - ua
 */
export function isMaybeBox(subnames, ua) {
    subnames = subnames.split(',');
    let len = subnames.length;
    while (len--) {
        if (isBox(subnames[len], ua) || isMatrix(subnames[len], ua)) {
            return true;
        }
    }
    return false;
}

export {
    version_compare,
    version_compare as versionCompare,
    getBoxVersion,
    isIOS,
    isAndroid,
    isBox,
    isMatrix
};
