/**
 * @file 获取iOS主题
 * @module device
 * @author panming <panming@baidu.com>
 */

import {invokeP} from '../invoke';
import {COMMAND_NOT_EXIST} from '../const';
import {isIOS, version_compare, getBoxVersion} from '../detect';
/**
 * @function avatarTip
 * @param {object} params - 调起参数
 * @return {Promise} promise - Promise
 */
export default (params = {}) => {
    const version = getBoxVersion();
    if (version_compare(version, '11.13') >= 0 && isIOS()) {
        return invokeP('theme/getTheme', params);
    }
    return Promise.reject(COMMAND_NOT_EXIST);
};
