/**
 * @file 摇一摇支持：加速器
 * @module device
 * @author wangyongqing <wangyongqing01@baidu.com>
 */
// http://ft.baidu.com/#/scheme/device_startaccelerometer
import {xRegister} from '../invoke';
/**
 * @function startAccelerometer
 * @param {function} cb - 回调函数
 * @param {number} interval - 防止回调太频繁设置的，回调间隔，>0.01
 */
export default (cb, interval = 0.2) => {

    return xRegister(
        {
            lite: {
                v: 34,
                versionRange: '3.9'
            },
            info: {
                v: 34,
                versionRange: '3.9'
            },
            iosmain: {
                v: 42,
                versionRange: '11.3.5'
            }
        },
        'device/startAccelerometer',
        {cb, interval: String(interval)}
    );
};
