import invoke from 'boxjs/dist/core/invoke';

export default function(obj) {
    function parasHandle(obj) {
        const names = obj.name.split('-');
        const [action, module] = names;

        const result = [module, action].join('/');
        return result;
    }
    return invoke(parasHandle(obj), obj.data, obj.abtest);
}
