/**
 * @file loadFile
 * @module data
 * @author panming <panming0@baidu.com>
 */

import {version_compare, getBoxVersion} from '../detect';
import {getParams} from '../utils';
import register from '../register';
import hybrid from './index';
register('feed/loadFile', {
    v: 6,
    versionRange: ['8.5'],
    liteVersionRange: ['2.3'],
    missionVersionRange: ['1.0'],
    androidinfoVersionRange: ['1.1'],
    notSupportFrame: {
        android: ['search']
    },
    filter() {
        return {
            module: 'feed',
            action: 'loadFile'
        };
    },
    thunk(res) {
        if (version_compare(getBoxVersion(), '9.2.5') >= 0) {
            return res;
        } else {
            return {
                status: 0,
                data: res
            };
        }
    }
});
/**
 * loadFile
 * @function loadFile
 */
export default data => hybrid(getParams('feed-loadFile', data));
