/**
 * 先来个简单的init
 * 写到h5方法时再扩展
 * @function init
 */
export default opt => {
    if (typeof window !== 'object') {
        return Promise.resolve();
    }
    return new Promise((resolve, reject) => {
        window.searchboxBoxjsId = opt.id;
        resolve({
            errCode: 0,
            errMsg: 'success'
        });
    });
};
