/**
 * @file 播放声音，用于破解webkit 非要点击下才能播放不行，音乐别太长，暂停不了
 * @module media
 * @author wangyongqing <wangyongqing01@baidu.com>
 */
import {xRegister} from '../invoke';
/**
 * 播放声音，用于破解webkit 非要点击下才能播放不行，音乐别太长，暂停不了
 * @function audioPlay
 * @param {string} url - 播放声音的 url
 */
export default url => {
    return xRegister(
        {
            lite: {
                v: 34,
                versionRange: '3.9'
            },
            info: {
                v: 34,
                versionRange: '3.9'
            },
            iosmain: {
                v: 42,
                versionRange: '11.3.5'
            }
        },
        'utils/playSound',
        {url}
    );
};
