/**
 * 常用的工具：对象 stringify
 * @file 常用的工具：对象 stringify
 * @function stringify
 * @param {object} json - 需要 stringify 的对象
 * @param {*} encode - 是否 encodeURIComponent 编码
 */
export default (json, encode = false) => {
    if (typeof json === 'object') {
        json = JSON.stringify(json);
    }
    if (encode) {
        json = encodeURIComponent(json);
    }
    return json;
};
