import register from '../register';
import {getParams} from '../utils';
import {close} from './index';

register('toast/close', {
    v: 11,
    hasCB: false,
    versionRange: ['9.1'],
    iosinfoVersionRange: ['1.1'],
    filter() {
        return {
            action: 'dismissToast',
            module: 'utils'
        };
    }
});
/**
 * @function closeToast
 * @param {Object} [params={}] - 参数
 */
export default (params = {}) => close(getParams('toast', params));
