/**
 * @file profile menu 同步个人主页菜单的选项
 * @module ui
 * @author panming <panming@baidu.com>
 */

import register from '../register';
import {
    getParams
} from '../utils';
import {open} from './index';

register('account/openProfileMenu', {
    v: 4,
    hasCB: true,
    versionRange: ['8.5'],
    filter: function () {
        return {
            module: 'account/profile',
            action: 'menu'
        };
    }
});
/**
 * @function openProfileMenu
 * @param {object} [data={}] - openProfile 参数
 */

export default (params = {}) => open(getParams('account-profileMenu', params));