import register from '../register';
import {getParams} from '../utils';
import {open} from './index';
import stringify from '../stringify';

register('redPacket/open', {
    v: 17,
    versionRange: ['10.1'],
    filter(data) {
        data.ext = stringify(data.ext);
        return {
            module: 'redPacket',
            action: 'getRedPacket',
            data: {
                params: stringify(data, true)
            }
        };
    }
});
/**
 * @function openRedPacket
 * @param {Object} [params={}] - 参数
 */
export default (params = {}) => open(getParams('redPacket', params));
