
/**
 * @file Created on 2018-12-10 触发点赞动画
 * @module ui/showPraiseMotion
 * @author panming <panming@baidu.com>
 */

import register from '../register';
import {getParams} from '../utils';
import {open} from './index';

register('praise/openMotionEvent', {
    v: 25,
    androidVersionRange: ['10.6.5'],
    iosVersionRange: ['10.10'],
    androidmissionVersionRange: ['1.0'],
    androidliteVersionRange: ['3.3'],
    filter: function () {
        return {
            module: 'praise',
            action: 'motionEvent'
        };
    }
});
/**
 * @function openPraiseMotionEvent
 * @param {Object} [params={}] - 参数
 */
export default (params = {}) => open(getParams('praise-motionEvent', params));
