export default class CloseSystemSuspense {
    constructor(api, pluginOptions) {
        this.api = api;
        this.pluginOptions = pluginOptions;
    }
    /**
     * 退出小窗->恢复落地页
     * run为本插件对应的方法实际执行的函数
     */
    run() {
        if (this.api.getMode() === 'na') {
            this.naRun();
        }
    }
    naRun() {
        // 仅支持IOS14以上系统
        const {isIOS, invokeP} = this.api.getBoxTools();
        if (isIOS()) {
            const containerId = this.api.getPlayerOption('containerId');
            invokeP('video/slrCloseSystemSuspensePlayer', {params: {containerId}});
        }
    }
}
