export default class CurrentTime {
    constructor(api, pluginOptions) {
        this.api = api;
        this.pluginOptions = pluginOptions;
    }
    /**
    * 当前播放进度
    */
    get() {
        const isIOS = this.api.getBoxTools().isIOS;
        if (this.api.getMode() === 'na' && isIOS()) {
            return this.api.getPlayerOption('currentTime');
        }
        const video = this.api.getVideoInstance();
        return video ? video.currentTime : '';
    }
    set(time) {
        const {isIOS, invokeP} = this.api.getBoxTools();
        if (this.api.getMode() === 'na' && isIOS()) {
            const {containerId, accurate} = this.api.getPlayerOption();
            invokeP('video/slrSeekPlayer', {
                params: {
                    containerId,
                    seconds: time,
                    accurate
                }
            });
            return;
        }
        const video = this.api.getVideoInstance();
        video && (video.currentTime = time);
    }
}

