export default class EnterSystemSuspense {
    constructor(api, pluginOptions) {
        this.api = api;
        this.pluginOptions = pluginOptions;
    }
    /**
     * 进入系统小窗ios专用
     * run为本插件对应的方法实际执行的函数
     */
    run() {
        const {isIOS, invokeP} = this.api.getBoxTools();
        if (this.api.getMode() === 'na' && isIOS()) {
            invokeP('video/slrEnterSystemSuspensePlayer', {
                params: {
                    containerId: this.api.getPlayerOption('containerId')
                }
            });
        }
    }
}
