export default class ExitSuspense {
    constructor(api, pluginOptions) {
        this.api = api;
        this.pluginOptions = pluginOptions;
    }
    /**
     * 退出小窗->恢复落地页
     * run为本插件对应的方法实际执行的函数
     */
    run() {
        if (this.api.getMode() === 'na') {
            this.naRun();
        }
    }
    naRun() {
        let {isIOS, isAndroid, invokeP} = this.api.getBoxTools();
        let containerId = this.api.getPlayerOption('containerId');
        if (isIOS()) {
            invokeP('video/slrExitSuspensePlayer', {params: {containerId}});
        }
        else if (isAndroid()) {
            const video = this.api.getVideoInstance();
            video.zeusVideoExt = JSON.stringify({
                id: containerId, // 唯一标识，必选
                name: 'h5samelayer', // 播放器类型，默认h5samelayer
                action: 'exitFloatingScreen' // 退出小窗模式，必选
            });
        }
    }
}
