export default class FullScreenType {
    constructor(api, pluginOptions) {
        this.api = api;
        this.pluginOptions = pluginOptions;
    }
    /**
    * 设置是否支持播放进度的回调,ios端属性
    */
    get() {
        const isIOS = this.api.getBoxTools().isIOS;
        if (this.api.getMode() === 'na' && isIOS()) {
            return this.api.getPlayerOption('fullScreenType');
        }
        return '';
    }
    set(type) {
        this.api.setPlayerOption('fullScreenType', type);
    }
}

