export default class IsIgnoreDeviceOrientation {
    constructor(api, pluginOptions) {
        this.api = api;
        this.pluginOptions = pluginOptions;
    }
    /**
    * IsIgnoreDeviceOrientation是否忽略设备旋转全屏ios端设置
    */
    get() {
        return !!this.api.getPlayerOption('isIgnoreDeviceOrientation');
    }
    set(isIgnore) {
        const {isIOS, invokeP} = this.api.getBoxTools();
        if (this.api.getMode() === 'na' && isIOS()) {
            const containerId = this.api.getPlayerOption('containerId');

            const fullScreenType = this.api.getPlayerOption('fullScreenType');
            this.api.setPlayerOption('isIgnoreDeviceOrientation', isIgnore);

            invokeP('video/slrSetIgnoreDeviceOrientation', {
                params: {
                    containerId,
                    isIgnoreDeviceOrientation: +isIgnore,
                    fullScreenType
                }
            });
        }
    }
}
