export default class Loop {
    constructor(api, pluginOptions) {
        this.api = api;
        this.pluginOptions = pluginOptions;
    }
    /**
     * loop循环次数
     */
    get() {
        const isIOS = this.api.getBoxTools().isIOS;
        if (this.api.getMode() === 'na' && isIOS()) {
            return this.api.getPlayerOption('loop');
        }
        const video = this.api.getVideoInstance();
        return video ? video.loop : '';
    }
    set(isTrue) {
        const {isIOS, invokeP} = this.api.getBoxTools();
        if (this.api.getMode() === 'na' && isIOS()) {
            const containerId = this.api.getPlayerOption('containerId');
            invokeP('video/slrSetLoop', {
                    params: {
                        containerId,
                        loop: +isTrue
                    }
                })
                .then(() => this.api.setPlayerOption('loop', isTrue));
            return;
        }
        const video = this.api.getVideoInstance();
        video && (video.loop = isTrue);
        this.api.setPlayerOption('loop', isTrue);
    }
}
