export default class NetworkState {
    constructor(api, pluginOptions) {
        this.api = api;
        this.pluginOptions = pluginOptions;
    }
    /**
     * 表示音频/视频元素的当前网络状态：
     * 0 = NETWORK_EMPTY - 音频/视频尚未初始化
     * 1 = NETWORK_IDLE - 音频/视频是活动的且已选取资源，但并未使用网络
     * 2 = NETWORK_LOADING - 浏览器正在下载数据
     * 3 = NETWORK_NO_SOURCE - 未找到音频/视频来源
     */
    get() {
        const isIOS = this.api.getBoxTools().isIOS;
        if (this.api.getMode() === 'na' && isIOS()) {
            return this.api.getPlayerOption('networkState');
        }
        const video = this.api.getVideoInstance();
        return video ? video.networkState : '';
    }
}
