export default class Pause {
    constructor(api, pluginOptions) {
        this.api = api;
        this.pluginOptions = pluginOptions;
    }
    /**
     * 暂停
     * run为本插件对应的方法实际执行的函数
     */
    run() {
        if (this.api.getMode() === 'na') {
            return this.naRun();
        }
        this.h5Run();
    }
    h5Run() {
        const video = this.api.getVideoInstance();
        // 兼容ios端h5模式出现的video.pause()报错问题
        return video && typeof video.pause === 'function' && video.pause();
    }
    naRun() {
        const {isIOS, isAndroid, invokeP} = this.api.getBoxTools();
        if (isIOS()) {
            const containerId = this.api.getPlayerOption('containerId');
            invokeP('video/slrPausePlayer', {params: {containerId}});
        }
        else if (isAndroid()) {
            this.h5Run();
        }
    }
}
