export default class PrefetchBoot {
    constructor(api, pluginOptions) {
        this.api = api;
        this.pluginOptions = pluginOptions;
    }
    /**
    * run为本插件对应的方法实际执行的函数
    */
    run(prefetchBootInfo) {
        if (this.api.getMode() === 'na') {
            this.naRun(prefetchBootInfo);
        }
    }
    naRun(prefetchBootInfo) {
        let {isIOS, invokeP} = this.api.getBoxTools();
        let containerId = this.api.getPlayerOption('containerId');
        if (isIOS()) {
            invokeP('video/slrPrefetchBootVideo', {
                params: {
                    containerId,
                    prefetchBootInfo
                }
            });
        }
    }
}