export default class Replay {
    constructor(api, pluginOptions) {
        this.api = api;
        this.pluginOptions = pluginOptions;
    }
    /**
     * 重新播放
     * run为本插件对应的方法实际执行的函数
     */
    run() {
        if (this.api.getMode() === 'na') {
            return this.naRun();
        }
        this.h5Run();
    }
    naRun() {
        const {isIOS, invokeP} = this.api.getBoxTools();
        const containerId = this.api.getPlayerOption('containerId');
        if (isIOS()) {
            invokeP('video/slrReplayPlayer', {params: {containerId}});
        }
        this.h5Run();
    }
    h5Run() {
        const video = this.api.getVideoInstance();
        if (video) {
            video.currentTime = 0;
            (video.paused || video.ended) && video.play();
        }
    }
}
