export default class SyncPlayerPosition {
    constructor(api, pluginOptions) {
        this.api = api;
        this.pluginOptions = pluginOptions;
    }
    /**
    * 设置是否支持播放进度的回调
    */
    get() {
        return !!this.api.getPlayerOption('syncPlayerPosition');
    }
    set(isTrue) {
        const {isIOS, invokeP} = this.api.getBoxTools();
        if (this.api.getMode() === 'na' && isIOS()) {
            const containerId = this.api.getPlayerOption('containerId');
            invokeP('video/slrEnableSyncPlayerPosition', {
                params: {
                    containerId,
                    syncPlayerPosition: +isTrue
                }
            });
            this.api.getPlayerOption('syncPlayerPosition', isTrue);
        }
    }
}

