
export default class TimeInterval {
    constructor(api, pluginOptions) {
        this.api = api;
        this.pluginOptions = pluginOptions;
    }
    get() {
        return this.api.getPlayerOption('timeInterval');
    }
    set(timeInterval) {
        const {isIOS, invokeP} = this.api.getBoxTools();
        if (this.api.getMode() === 'na' && isIOS()) {
            let containerId = this.api.getPlayerOption('containerId');
            invokeP('video/slrSetVideoProgressTimeInterval', {
                    params: {
                        containerId,
                        timeInterval
                    }
                })
                .then(() => this.api.setPlayerOption('timeInterval', timeInterval));
            return;
        }
    }
}
