export default class Volume {
    constructor(api, pluginOptions) {
        this.api = api;
        this.pluginOptions = pluginOptions;
    }
    /**
    * volume声音
    */
    get() {
        // 端内统一用option来维护volume值，安卓端video.volume用于更改
        if (this.api.getMode() === 'na') {
            return this.api.getPlayerOption('volume');
        }
        const video = this.api.getVideoInstance();
        return video ? video.volume : '';
    }
    set(vol) {
        const {isIOS, invokeP} = this.api.getBoxTools();
        const containerId = this.api.getPlayerOption('containerId');
        if (this.api.getMode() === 'na' && isIOS()) {
            invokeP('video/slrSetSystemVolume', {
                    params: {
                        containerId,
                        volume: vol
                    }
                })
                .then(() => this.api.setPlayerOption('volume', vol));
            return;
        }
        const video = this.api.getVideoInstance();
        video && (video.volume = vol);
    }
}
