import {
    isIOS,
    isAndroid,
    isMainBox,
    invokeP,
    versionCompare,
    version
} from './utils/index';

export default prefetchInfo => {
    if (isMainBox() && versionCompare(version, '12.6') >= 0) {
        const head = isIOS()
            ? 'video/slrPrefetchVideo'
            : isAndroid() ? 'videoplayer/slrPrefetchVideo' : '';
        head && invokeP(head, {
            params: {
                prefetchInfo
            }
        });
    }
};
