/**
 * @file dom ready 方法
 * @module browser
 * @author wangyongqing <wangyongqing01@baidu.com>
 */
const fnList = [];
let isReady = false;
function onReady() {
    fnList.forEach(fn => {
        fn();
    });
    fnList.length = 0;
    isReady = true;
}
/**
 * dom ready 方法
 * @function ready
 * @param {function} fn - 回调函数
 */
export default function ready(fn) {
    if (typeof fn !== 'function') {
        return;
    }

    if (isReady) {
        fn();
    }
    else {
        fnList.push(fn);
    }
}
if ('complete,loaded,interactive'.indexOf(document.readyState) > -1 && document.body) {
    onReady();
}
else {
    document.addEventListener('DOMContentLoaded', onReady, false);
}

