/**
 * 发送时长日志
 * @param {object} 时长日志开始时间，日志id，及参数
 */
import pblog from './pblog';

export default ({starttime, id, option}) => {
    if (!starttime || starttime.toString().length < 13) {
        return;
    }
    let endtime = +new Date();
    const params = {
        id,
        type: 1,
        content: {
            duration: ((endtime - starttime) / 1000).toFixed(2) + '',
            option
        },
        starttime,
        endtime
    };
    pblog('newEvent', [params, 99]);
};
