const doc = document;
const parentNode = doc.head || doc.body || doc.getElementsByTagName('script')[0];
/**
 * 给页面添加CSS样式
 * @function addStyle
 * @param  {String} url - CSS链接或者是CSS样式
 */
export default url => {
    if (/.+\.css$/.test(url)) {
        const style = doc.createElement('link');
        style.type = 'text/css';
        style.rel = 'stylesheet';
        style.href = url;
        parentNode.appendChild(style);
    } else {
        const style = document.createElement('style');
        style.type = 'text/css';
        parentNode.appendChild(style);
        style.innerHTML = url;
    }
};
