module.exports = function () {
  /**
  * @file
  * @author renzhonghua
  */
  var invoke = require("boxjs/dist/core/invoke");

  function paramsHandle(obj, type) {
    var action = [type, obj.type].join('/');
    return invoke(action, obj.data, obj.abtest);
  }

  return {
    video: function (obj) {
      return paramsHandle(obj, 'video');
    },
    tts: function (obj) {
      /* istanbul ignore next */
      return paramsHandle(obj, 'tts');
    }
  };
}();