module.exports = function () {
  var $ = require('boxjs/dist/utils/_bdboxWrapper');
  /*jshint -W802 */


  var loadjs = require('../io/loadJS');

  var url = 'http://box.bdimg.com/??';

  var load = function (files, success, error, timeout) {
    return loadjs({
      url: url + $.toArray(files).join(','),
      success: success,
      error: error,
      timeout: timeout | 0
    });
  };

  load.set = function (n) {
    url = n;
  };

  return load;
}();