module.exports = function () {
  /**
  * @file oauthApi.js
  * @author renzhonghua@baidu.com
  * @description 鉴权接口
  */
  var invoke = require("boxjs/dist/core/invoke");

  return function (obj) {
    var action = obj.name.replace(/-/g, '/');
    var data = obj.data;
    return invoke(action, data);
  };
}();